SELECT
  *
FROM
  cook_mst
WHERE
  user_id = #{userId}
AND
  (
    0 = 0

    AND cook_id = #{cookId}


    AND cook_name LIKE CONCAT('%', #{cookName}, '%')


    AND cook_price >= #{minPrice}


    AND cook_price <= #{maxPrice}


    AND cook_id IN
      (
        SELECT
          cook_id
        FROM
          cook_food_tbl
        WHERE
          food_id IN

            #{foodId}

      )

  )
OR
  cook_flg = 'TRUE'
AND
  (
    0 = 0

    AND user_id = #{selectUserId}


    AND cook_id = #{cookId}


    AND cook_name LIKE CONCAT('%', #{cookName}, '%')


    AND cook_price > #{minPrice}


    AND cook_price < #{maxPrice}


    AND cook_id IN
      (
        SELECT
          cook_id
        FROM
          cook_food_tbl
        WHERE
          food_id IN

            #{foodId}

      )

  )
ORDER BY

  cook_id


  cook_name


  cook_price


  ASC


  DESC

LIMIT
  #{limit}
OFFSET
  #{offset}
;
